using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.UI; using UnityEngine.SceneManagement;  public class IntroSceneController : MonoBehaviour {  	public Button easyButton; 	public Button mediumButton; 	public Button hardButton;  	public SoundManager soundManager; 	public AudioClip buttonSound;  	// Use this for initialization 	void Start () { 		Button easyButtonCR = easyButton.GetComponent<Button>(); 		easyButtonCR.onClick.AddListener(loadEasy);  		Button mediumButtonCR = mediumButton.GetComponent<Button>(); 		mediumButtonCR.onClick.AddListener(loadMedium);  		Button hardButtonCR = hardButton.GetComponent<Button>(); 		hardButtonCR.onClick.AddListener(loadHard); 	}  	private void loadEasy() { 		soundManager.PlayButtonSound (buttonSound); 		GameManager.difficulty = Difficulty.easy; 		SceneManager.LoadScene (1); 	}  	private void loadMedium() { 		soundManager.PlayButtonSound (buttonSound); 		GameManager.difficulty = Difficulty.medium; 		SceneManager.LoadScene (1); 	}  	private void loadHard() { 		soundManager.PlayButtonSound (buttonSound); 		GameManager.difficulty = Difficulty.hard; 		SceneManager.LoadScene (1); 	}  	// Update is called once per frame 	void Update () {  	} }