	public GameObject note; //stores prefab of the note
	public GameObject obstacle; //stores prefab of the obstacle
	public float yPosNote;
	public float yPosObstacle;
	public float zPosInit;
	public float zPosFinal;
	public float xOffset; 

	public LevelManager levelManager;

	public Coroutine spawnNotesCoroutine;
	public Coroutine spawnObstaclesCoroutine;

	private Vector3 leftNote;
	private Vector3 centerNote;
	private Vector3 rightNote;

	private Vector3 leftObstacle;
	private Vector3 centerObstacle;
	private Vector3 rightObstacle;

	private bool timerResetNotes = true;
	private bool timerResetObstacles = true; 
	private int count = 0;
	public string notes;

	public int totalNotes = 0;

	public bool generateNotes = true;

	private int noteLane=0; 

	// Use this for initialization
	void Start () {
		leftNote = new Vector3 (-1*xOffset, yPosNote, zPosFinal);
		centerNote = new Vector3 (0f, yPosNote, zPosFinal);
		rightNote = new Vector3 (xOffset, yPosNote, zPosFinal);

		leftObstacle = new Vector3 (-1*xOffset, yPosObstacle, zPosFinal);
		centerObstacle = new Vector3 (0f, yPosObstacle, zPosFinal);
		rightObstacle = new Vector3 (xOffset, yPosObstacle, zPosFinal);
	}

	// Update is called once per frame
	void Update () {
		if (timerResetNotes) {
			switch (GameManager.difficulty) {
			case Difficulty.easy:
				if (patternEasy [count] == 0 || patternEasy [count] == 1 || patternEasy [count] == 2) {
					spawnNotesCoroutine = StartCoroutine (spawnNotes ());
				}
				break;
			case Difficulty.medium:
				if (patternMed [count] == 0 || patternMed [count] == 1 || patternMed [count] == 2) {
					spawnNotesCoroutine = StartCoroutine (spawnNotes ());
				}
				break;
			case Difficulty.hard:
				if (patternHard [count] == 0 || patternHard [count] == 1 || patternHard [count] == 2) {
					spawnNotesCoroutine = StartCoroutine (spawnNotes ());
				}
				break;
			}
		}
		if (timerResetObstacles) {
			spawnObstaclesCoroutine = StartCoroutine (spawnObstacles ());
//			timerResetObstacles = false;
		}

		count++;
	}

	//coroutine
	IEnumerator spawnObstacles () {
		if (generateNotes) {
			int index = 0;

			switch (GameManager.difficulty) {
			case Difficulty.easy:
				index = Random.Range (1, 1200);
				break;
			case Difficulty.medium:
				index = Random.Range (1, 750);
				break;
			case Difficulty.hard:
				index = Random.Range (1, 200);
				break;
			}

			//check if note was produced in lane at that frame
			if (index == noteLane-1)
				yield break;

			if (count > 6 && count < patternEasy.Count - 6) {
				for (int i = count - 5; i < count + 5; i++) {
					if (i == noteLane) {
						yield break; 
					}
				}
			}

			float xPos = 0;
			switch (index) {
			case 1:
				xPos = -1 * xOffset;
				break;
			case 2:
				xPos = 0.0f;
				break;
			case 3:
				xPos = xOffset;
				break;
			default:
				yield break;
			}
			
			GameObject obstaclePrefab = Instantiate (obstacle, new Vector3 (xPos, yPosObstacle, zPosInit), obstacle.transform.rotation);

			switch (index) {
			case 1:
				iTween.MoveTo (obstaclePrefab, leftObstacle, 8f);
				break;
			case 2:
				iTween.MoveTo (obstaclePrefab, centerObstacle, 8f);
				break;
			case 3:
				iTween.MoveTo (obstaclePrefab, rightObstacle, 8f);
				break;
			default:
				break;
			}

			timerResetObstacles = true;
			if (count == patternEasy.Count - 6)
				timerResetNotes = false;
		}

		yield break; 
	}

	//coroutine
	IEnumerator spawnNotes () {
		//yield return new WaitForSeconds (0f);
		if (generateNotes) {
			int index = Random.Range (1, 3);

			float xPos = 0;
			switch (index) {
			case 1:
				//xPos = 0.0f;
				xPos = -1*xOffset;
				break;
			case 2:
				xPos = 0.0f;
				break;
			case 3:
				xPos = xOffset;
				//xPos = 0.0f;

				break;
			}

			GameObject notePrefab = Instantiate (note, new Vector3 (xPos, yPosNote, zPosInit), note.transform.rotation);
			totalNotes += 1;
			switch (index) {
			case 1:
				iTween.MoveTo (notePrefab, leftNote, 8f);
			//iTween.MoveTo (notePrefab, centerNote, 8f);
				noteLane = 1;
				break;
			case 2:
				iTween.MoveTo (notePrefab, centerNote, 8f);
				noteLane = 2;
				break;
			case 3:
				iTween.MoveTo (notePrefab, rightNote, 8f);
			//iTween.MoveTo (notePrefab, centerNote, 8f);
				noteLane = 3;
				break;
			}

			timerResetNotes = true;
			switch (GameManager.difficulty) {
			case Difficulty.easy:
				if (count == patternEasy.Count - 1) {
					timerResetNotes = false;
				}
				break;
			case Difficulty.medium:
				if (count == patternMed.Count - 1) {
					timerResetNotes = false;
				}
				break;
			case Difficulty.hard:
				if (count == patternHard.Count - 1) {
					timerResetNotes = false;
				}
				break;
			}
		}

		yield break;
	}
}